// Button.tsx (React + TS) - css-vars engine
import React from "react";
import "./btn.css";

type Variant = "solid"|"outline"|"ghost"|"soft"|"link";
type Tone = "primary"|"neutral"|"success"|"warning"|"danger"|"info";
type Size = "xs"|"sm"|"md"|"lg"|"xl";
type Shape = "rounded"|"pill"|"square";

export type ButtonProps = React.ButtonHTMLAttributes<HTMLButtonElement> & {
  as?: "button"|"a";
  href?: string;
  variant?: Variant;
  tone?: Tone;
  size?: Size;
  shape?: Shape;
  fullWidth?: boolean;
  loading?: boolean;
  iconOnly?: boolean;
  ariaLabel?: string;
  trackingId?: string;
};

export function Button({
  as="button",
  href,
  variant="solid",
  tone="primary",
  size="md",
  shape="rounded",
  fullWidth=false,
  loading=false,
  disabled=false,
  iconOnly=false,
  ariaLabel,
  trackingId,
  children,
  ...rest
}: ButtonProps){
  const isDisabled = disabled || loading;
  const cls = [
    "btn",
    `btn--${variant}`,
    `btn--${tone}`,
    `btn--${size}`,
    `btn--${shape}`,
    fullWidth ? "btn--full" : "",
    isDisabled ? "btn--disabled" : "",
    loading ? "btn--loading" : "",
    iconOnly ? "btn--iconOnly" : ""
  ].filter(Boolean).join(" ");

  if(iconOnly && !ariaLabel){
    console.warn("Button: ariaLabel is required when iconOnly=true");
  }

  if(as === "a"){
    const rel = (rest as any).target === "_blank"
      ? `${((rest as any).rel || "")} noopener noreferrer`.trim()
      : (rest as any).rel;
    return (
      <a
        href={href || "#"}
        className={cls}
        aria-label={ariaLabel}
        aria-disabled={isDisabled ? "true" : undefined}
        data-tracking-id={trackingId || undefined}
        rel={rel}
        onClick={(e) => { if(isDisabled) e.preventDefault(); (rest as any).onClick?.(e); }}
        {...(rest as any)}
      >
        {loading ? <span className="btn__loader" aria-hidden="true" /> : null}
        <span className="btn__content">{children}</span>
      </a>
    );
  }

  return (
    <button
      type={(rest as any).type || "button"}
      className={cls}
      disabled={isDisabled}
      aria-busy={loading ? "true" : undefined}
      aria-label={ariaLabel}
      data-tracking-id={trackingId || undefined}
      {...rest}
    >
      {loading ? <span className="btn__loader" aria-hidden="true" /> : null}
      <span className="btn__content">{children}</span>
    </button>
  );
}

react_native_expo_nativewind.template:
// Button.tsx (React Native + NativeWind)
import React from "react";
import { Pressable, Text, View } from "react-native";

type Variant = "solid"|"outline"|"ghost"|"soft"|"link";
type Tone = "primary"|"neutral"|"success"|"warning"|"danger"|"info";
type Size = "xs"|"sm"|"md"|"lg"|"xl";

const sizeMap: Record<Size, string> = {
  xs: "px-2 py-1 text-xs min-h-[28px]",
  sm: "px-2.5 py-2 text-sm min-h-[34px]",
  md: "px-3 py-2.5 text-sm min-h-[40px]",
  lg: "px-3.5 py-3 text-base min-h-[46px]",
  xl: "px-4 py-3.5 text-lg min-h-[52px]"
};

// You can route tone colors through NativeWind theme config.
const toneMap: Record<Tone, {bg:string; fg:string; border:string}> = {
  primary: { bg: "bg-blue-600", fg: "text-white", border: "border-blue-600" },
  neutral: { bg: "bg-zinc-200", fg: "text-zinc-900", border: "border-zinc-300" },
  success: { bg: "bg-green-600", fg: "text-white", border: "border-green-600" },
  warning: { bg: "bg-amber-600", fg: "text-white", border: "border-amber-600" },
  danger:  { bg: "bg-red-600", fg: "text-white", border: "border-red-600" },
  info:    { bg: "bg-sky-600", fg: "text-white", border: "border-sky-600" }
};

export function Button({
  variant="solid",
  tone="primary",
  size="md",
  fullWidth=false,
  loading=false,
  disabled=false,
  accessibilityLabel,
  children,
  onPress
}: {
  variant?: Variant;
  tone?: Tone;
  size?: Size;
  fullWidth?: boolean;
  loading?: boolean;
  disabled?: boolean;
  accessibilityLabel?: string;
  children?: React.ReactNode;
  onPress?: () => void;
}) {
  const isDisabled = disabled || loading;
  const toneCls = toneMap[tone];

  const variantCls =
    variant === "solid" ? `${toneCls.bg} ${toneCls.fg}` :
    variant === "outline" ? `bg-transparent border ${toneCls.border} ${toneCls.fg.replace("text-white","text-blue-600")}` :
    variant === "link" ? `bg-transparent ${toneCls.fg.replace("text-white","text-blue-600")} underline` :
    variant === "soft" ? `bg-blue-600/10 ${toneCls.fg.replace("text-white","text-blue-700")}` :
    `bg-transparent ${toneCls.fg.replace("text-white","text-blue-700")}`;

  const cls = [
    "rounded-xl",
    "items-center",
    "justify-center",
    "flex-row",
    "gap-2",
    "active:opacity-90",
    "opacity-100",
    sizeMap[size],
    variantCls,
    fullWidth ? "w-full" : "",
    isDisabled ? "opacity-60" : ""
  ].filter(Boolean).join(" ");

  return (
    <Pressable
      className={cls}
      disabled={isDisabled}
      accessibilityRole="button"
      accessibilityLabel={accessibilityLabel}
      onPress={onPress}
    >
      {loading ? <View className="w-4 h-4 rounded-full border border-white/60" /> : null}
      <Text className="font-semibold">{children}</Text>
    </Pressable>
  );
}

flutter_dart.template:
// button.dart (Flutter) - Theme-driven
import 'package:flutter/material.dart';

enum BtnVariant { solid, outline, ghost, soft, link }
enum BtnTone { primary, neutral, success, warning, danger, info }
enum BtnSize { xs, sm, md, lg, xl }

class AtomButton extends StatelessWidget {
  final BtnVariant variant;
  final BtnTone tone;
  final BtnSize size;
  final bool fullWidth;
  final bool loading;
  final bool disabled;
  final VoidCallback? onPressed;
  final Widget child;

  const AtomButton({
    super.key,
    this.variant = BtnVariant.solid,
    this.tone = BtnTone.primary,
    this.size = BtnSize.md,
    this.fullWidth = false,
    this.loading = false,
    this.disabled = false,
    required this.child,
    this.onPressed,
  });

  @override
  Widget build(BuildContext context) {
    final isDisabled = disabled || loading;
    final minH = switch(size){
      BtnSize.xs => 28.0,
      BtnSize.sm => 34.0,
      BtnSize.md => 40.0,
      BtnSize.lg => 46.0,
      BtnSize.xl => 52.0,
    };
    final pad = switch(size){
      BtnSize.xs => const EdgeInsets.symmetric(horizontal: 8, vertical: 6),
      BtnSize.sm => const EdgeInsets.symmetric(horizontal: 10, vertical: 8),
      BtnSize.md => const EdgeInsets.symmetric(horizontal: 12, vertical: 10),
      BtnSize.lg => const EdgeInsets.symmetric(horizontal: 14, vertical: 12),
      BtnSize.xl => const EdgeInsets.symmetric(horizontal: 16, vertical: 14),
    };

    // Map tones to your ThemeExtension or ColorScheme
    final scheme = Theme.of(context).colorScheme;
    final toneBg = switch(tone){
      BtnTone.primary => scheme.primary,
      BtnTone.neutral => scheme.surfaceVariant,
      BtnTone.success => Colors.green,
      BtnTone.warning => Colors.orange,
      BtnTone.danger => Colors.red,
      BtnTone.info => Colors.lightBlue,
    };

    ButtonStyle style;
    switch(variant){
      case BtnVariant.solid:
        style = ElevatedButton.styleFrom(
          backgroundColor: toneBg,
          foregroundColor: Colors.white,
          padding: pad,
          minimumSize: Size(fullWidth ? double.infinity : 0, minH),
          shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(14)),
        );
        break;
      case BtnVariant.outline:
        style = OutlinedButton.styleFrom(
          foregroundColor: toneBg,
          side: BorderSide(color: toneBg),
          padding: pad,
          minimumSize: Size(fullWidth ? double.infinity : 0, minH),
          shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(14)),
        );
        break;
      default:
        style = TextButton.styleFrom(
          foregroundColor: toneBg,
          padding: pad,
          minimumSize: Size(fullWidth ? double.infinity : 0, minH),
        );
    }

    return SizedBox(
      height: minH,
      child: ElevatedButton(
        style: style,
        onPressed: isDisabled ? null : onPressed,
        child: loading
          ? const SizedBox(width: 16, height: 16, child: CircularProgressIndicator(strokeWidth: 2))
          : child,
      ),
    );
  }
}
