import React from "react";
import "./select.css";

type Variant = "outline"|"filled"|"ghost"|"underline";
type Tone = "neutral"|"primary"|"success"|"warning"|"danger"|"info";
type Size = "xs"|"sm"|"md"|"lg"|"xl";

export type Option = { value: string; label: string; disabled?: boolean; group?: string; };

export function Select({
  label,
  options,
  value,
  onChange,
  placeholder="Select...",
  size="md",
  density="comfortable",
  variant="outline",
  tone="neutral",
  disabled=false,
  invalid=false,
  clearable=false,
  searchable=false,
  ...rest
}:{
  label?: React.ReactNode;
  options: Option[];
  value?: string;
  onChange?: (v:string)=>void;
  placeholder?: string;
  size?: Size;
  density?: "comfortable"|"compact";
  variant?: Variant;
  tone?: Tone;
  disabled?: boolean;
  invalid?: boolean;
  clearable?: boolean;
  searchable?: boolean;
} & React.HTMLAttributes<HTMLDivElement>){
  const [open,setOpen] = React.useState(false);
  const [q,setQ] = React.useState("");
  const btnId = React.useId();
  const listId = `${btnId}-list`;

  const selected = options.find(o=>o.value===value);
  const filtered = !searchable || !q ? options : options.filter(o=>o.label.toLowerCase().includes(q.toLowerCase()));

  const cls = ["sf",`sf--${variant}`,`sf--${tone}`,`sf--${size}`,`sf--${density}`,invalid?"sf--invalid":"",disabled?"sf--disabled":"",open?"sf--open":""].filter(Boolean).join(" ");

  return (
    <div className="sf__wrap" {...rest}>
      {label ? <div className="sf__label">{label}</div> : null}
      <button
        type="button"
        className={cls}
        id={btnId}
        aria-haspopup="listbox"
        aria-expanded={open ? "true" : "false"}
        aria-controls={listId}
        disabled={disabled}
        onClick={()=> setOpen(o=>!o)}
      >
        <span className="sf__value">{selected ? selected.label : <span className="sf__placeholder">{placeholder}</span>}</span>
        {clearable && value ? (
          <span className="sf__clear" aria-hidden="true" onClick={(e)=>{e.stopPropagation(); onChange?.("");}}>×</span>
        ) : null}
        <span className="sf__chev" aria-hidden="true">▾</span>
      </button>

      {open ? (
        <div className="sf__menu" role="listbox" id={listId} aria-labelledby={btnId}>
          {searchable ? (
            <div className="sf__search">
              <input value={q} onChange={(e)=>setQ(e.target.value)} placeholder="Search..." />
            </div>
          ) : null}
          {filtered.length ? filtered.map(opt => (
            <div
              key={opt.value}
              role="option"
              aria-selected={opt.value===value ? "true":"false"}
              className={"sf__item" + (opt.value===value ? " is-selected":"") + (opt.disabled ? " is-disabled":"")}
              onClick={()=>{ if(opt.disabled) return; onChange?.(opt.value); setOpen(false);}}
            >{opt.label}</div>
          )) : <div className="sf__empty">No results</div>}
        </div>
      ) : null}
    </div>
  );
}
